async function swap(signature, swapData) {
  if(signature!= null && account!=null && swapData!=null){  
    const { v, r, s } = _ethers.utils.splitSignature(signature);
    console.log(v,r,s);
    let approver = account;
    console.log(signature,approver);

    const callData1 = USDCContract.interface.encodeFunctionData('permit', [
      approver,
      RouterContractAddress,
      1000000,
      2661766724,
      v,
      r,
      s
    ]);

    const callData2 = USDCContract.interface.encodeFunctionData('transferFrom', [
      approver,
      RouterContractAddress,
      1000000,
    ]);

    const callData3 = USDCContract.interface.encodeFunctionData('approve', [
      SwapperContractAddress,
      1000000,
    ]);

    
    const callData = [
      {
        "target": USDCContractAddress,                 
        "callData": callData1,
      },
      {
        "target": USDCContractAddress,                 
        "callData": callData2
      },
      {
        "target": USDCContractAddress,                 
        "callData": callData3
      },
      {
        "target": SwapperContractAddress,                 
        "callData": swapData
      }
    ]

    const callData4 = RouterContract.interface.encodeFunctionData('aggregate', [
      callData
    ]);
    console.log(callData4)
    const gasLimit = await provider.estimateGas({
      from: approver,
      to: RouterContractAddress,
      data: callData4,
    });
    
    console.log('Gas limit:', gasLimit.toString());
    let _params = {
      method: 'eth_sendTransaction',
      params: [
        {
          from: account, // The user's active address.
          to: RouterContractAddress, // Required except during contract publications.
          value: '0', // Only required to send ether to the recipient from the initiating external account.
          data: callData4, // Optional, but used for defining smart contract creation and interaction.
        },
      ],
    }
    return await window.ethereum.request(_params)
  }
}

